import React from 'react';

import { useGetHome } from './_query';
import { useParams } from 'react-router-dom';
import { NavLink } from 'react-router-dom';
import Layout from '../components/layout'
import style from "./index.module.css"

export const Home = () => {
  const { data, isLoading } = useGetHome()
  const { id } = useParams

  return (
    <Layout>
    <div className={style.container}>
     <div className={style.mainImageContainer}> <img src={data?.mainimage?.sourceUrl} alt={data?.mainimage?.altText} className={style.mainImage}/></div>

      <div className={style.miniContainer}>
        <h2>{data?.title}</h2>
        <div>{data?.description}</div>
      </div>

      <h1>Top 10 gerechten</h1>
      <div className={style.gerechtenContainer}>
        {data?.gerechten?.map((item) =>
          <div  key={item.slug} className={style.gerechtenMiniContainer}>
            <NavLink to={`/gerechten/${item.slug}`} className={style.link}>
              <img src={item.gerechtmeta.profilePicture?.sourceUrl} className={style.gerechtenImage} />
              <div className={style.gerechtTitle}>{item.gerechtmeta.title}</div>
            </NavLink>
          </div>
        )}
      </div>
    </div>
  </Layout>
  );
}
