import React, { useState } from 'react';
import Modal from 'react-modal';
import { useGetAbout } from './_query'
import Layout from '../components/layout'

import style from './about.module.css'

export const About = () => {
  const { data, isLoading } = useGetAbout()
  const [pictures, setPictures] = useState[
    { picture: data?.personeel1?.sourceUrl, altText: data?.personeel1?.altText },
    { picture: data?.personeel2?.sourceUrl, altText: data?.personeel2?.altText },
    { picture: data?.personeel3?.sourceUrl, altText: data?.personeel3?.altText },
    { picture: data?.sfeer1?.sourceUrl, altText: data?.sfeer1?.altText },
    { picture: data?.sfeer2?.sourceUrl, altText: data?.sfeer2?.altText },
    { picture: data?.sfeer3?.sourceUrl, altText: data?.sfeer3?.altText },
    { picture: data?.sfeer4?.sourceUrl, altText: data?.sfeer4?.altText }
  ]
  const [modalIsOpen, setIsOpen] = React.useState(false)
  const [modalIsOpen2, setIsOpen2] = React.useState(false)
  const [modalIsOpen3, setIsOpen3] = React.useState(false)
  const [modalIsOpen4, setIsOpen4] = React.useState(false)
  const [modalIsOpen5, setIsOpen5] = React.useState(false)
  const [modalIsOpen6, setIsOpen6] = React.useState(false)
  const [modalIsOpen7, setIsOpen7] = React.useState(false)
  const [modalIsOpen8, setIsOpen8] = React.useState(false)

  //const goalPicture = getImage(about.goalPicture.localFile)
  const personeel1 = data?.personeel1.sourceUrl
  const personeel2 = data?.personeel2.sourceUrl
  const personeel3 = data?.personeel3.sourceUrl
  const personeel4 = data?.personeel4.sourceUrl

  const sfeer1 = data?.sfeer1.sourceUrl
  const sfeer2 = data?.sfeer2.sourceUrl
  const sfeer3 = data?.sfeer3.sourceUrl
  const sfeer4 = data?.sfeer4.sourceUrl

  const customStyles = {
    content: {
      top: '53%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      backgroundColor: "rgb(233, 229, 229)"
    },
  };
  return (
    <Layout>
      <div className={style.container}>
        <h2 className={style.title}>{data?.goalTitle}</h2>
        <div className={style.goalContainer}>

          {pictures && pictures?.map((item) => <img src={item.picture} altText={item.altText}/>)}

          <div className={style.goalDescription}>{data?.goalDescription}</div>
          <div className={style.groupGoalPic}>
            <div onClick={() => setIsOpen(true)} style={{ cursor: "pointer" }} className={style.clickableImages} ><img src={personeel1} alt={data?.goalPicture.altText} className={style.goalImage} /></div>
            <div onClick={() => setIsOpen2(true)} style={{ cursor: "pointer" }} className={style.clickableImages}><img src={personeel2} alt={data?.goalPicture.altText} className={style.goalImage} /></div>
            <div onClick={() => setIsOpen3(true)} style={{ cursor: "pointer" }} className={style.clickableImages}><img src={personeel3} alt={data?.goalPicture.altText} className={style.goalImage} /></div>
