import React, { useState } from 'react';
import Modal from 'react-modal';
import { useGetAbout } from './_query'
import Layout from '../components/layout'

import style from './about.module.css'

export const About = () => {
  const { data, isLoading } = useGetAbout()
  const [picturesPersoneel, setPicturesPersoneel] = useState([
    { picture: data?.personeel1.sourceUrl, altText: data?.personeel1.altText },
    { picture: data?.personeel2.sourceUrl, altText: data?.personeel2.altText },
    { picture: data?.personeel3.sourceUrl, altText: data?.personeel3.altText },
    { picture: data?.sfeer1.sourceUrl, altText: data?.sfeer1.altText },
    { picture: data?.sfeer2.sourceUrl, altText: data?.sfeer2.altText },
    { picture: data?.sfeer3.sourceUrl, altText: data?.sfeer3.altText },
    { picture: data?.sfeer4.sourceUrl, altText: data?.sfeer4.altText }
  ])

  const [picturesSfeer, setPicturesSfeer] = useState([
    { picture: data?.sfeer1.sourceUrl, altText: data?.sfeer1.altText },
    { picture: data?.sfeer2.sourceUrl, altText: data?.sfeer2.altText },
    { picture: data?.sfeer3.sourceUrl, altText: data?.sfeer3.altText },
    { picture: data?.sfeer4.sourceUrl, altText: data?.sfeer4.altText }
  ])
  const [modalIsOpen, setIsOpen] = React.useState(false)
  const [modalIsOpen2, setIsOpen2] = React.useState(false)
  const [modalIsOpen3, setIsOpen3] = React.useState(false)
  const [modalIsOpen4, setIsOpen4] = React.useState(false)
  const [modalIsOpen5, setIsOpen5] = React.useState(false)
  const [modalIsOpen6, setIsOpen6] = React.useState(false)
  const [modalIsOpen7, setIsOpen7] = React.useState(false)
  const [modalIsOpen8, setIsOpen8] = React.useState(false)

  //const goalPicture = getImage(about.goalPicture.localFile)
  const personeel1 = data?.personeel1.sourceUrl
  const personeel2 = data?.personeel2.sourceUrl
  const personeel3 = data?.personeel3.sourceUrl
  const personeel4 = data?.personeel4.sourceUrl

  const sfeer1 = data?.sfeer1.sourceUrl
  const sfeer2 = data?.sfeer2.sourceUrl
  const sfeer3 = data?.sfeer3.sourceUrl
  const sfeer4 = data?.sfeer4.sourceUrl

  const customStyles = {
    content: {
      top: '53%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      backgroundColor: "rgb(233, 229, 229)"
    },
  };
  return (
    <Layout>
      <div className={style.container}>
        <h2 className={style.title}>{data?.goalTitle}</h2>
        <div className={style.goalContainer}>


          {/* <div className={style.goalDescription}>{data?.goalDescription}</div> */}
          {picturesPersoneel && pictures?.map((item) =>
            
        
              <img key={item.altText} src={item.picture} alt={item.altText} className={style.clickableImages} />

            )}
          {/* <div className={style.groupGoalPic}>
            <div onClick={() => setIsOpen(true)} style={{ cursor: "pointer" }} className={style.clickableImages} ><img src={personeel1} alt={data?.goalPicture.altText} className={style.goalImage} /></div>
            <div onClick={() => setIsOpen2(true)} style={{ cursor: "pointer" }} className={style.clickableImages}><img src={personeel2} alt={data?.goalPicture.altText} className={style.goalImage} /></div>
            <div onClick={() => setIsOpen3(true)} style={{ cursor: "pointer" }} className={style.clickableImages}><img src={personeel3} alt={data?.goalPicture.altText} className={style.goalImage} /></div>
            <div onClick={() => setIsOpen4(true)} style={{ cursor: "pointer" }} className={style.clickableImages}> <img src={personeel4} alt={data?.goalPicture.altText} className={style.goalImage} /></div>
          </div> */}
        </div>

        <h2 className={style.missionTitle}>{data?.missionTitle}</h2>
        <div className={style.missionContainer}>
          <div className={style.groupMissionPic}>

            {/* <div onClick={() => setIsOpen5(true)} style={{ cursor: "pointer" }} className={style.toughableImage}> <img src={sfeer1} alt={data?.sfeer1.altText} className={style.missionsrc} /></div>
            <div onClick={() => setIsOpen6(true)} style={{ cursor: "pointer" }} className={style.toughableImage}>  <img src={sfeer2} alt={data?.sfeer2.altText} className={style.missionImage} /></div>
            <div onClick={() => setIsOpen7(true)} style={{ cursor: "pointer" }} className={style.toughableImage}><img src={sfeer3} alt={data?.sfeer3.altText} className={style.missionImage} /></div>
            <div onClick={() => setIsOpen8(true)} style={{ cursor: "pointer" }} className={style.toughableImage}> <img src={sfeer4} alt={data?.sfeer4.altText} className={style.missionImage} /></div> */}

          </div>
          {/* <div className={style.missionDescription}>{data?.missionDescription}</div> */}
        </div>

        <Modal
          isOpen={modalIsOpen}
          style={customStyles}
          onClose={modalIsOpen}
          onRequestClose={modalIsOpen}

        >
          <div ><img src={personeel1} alt={data?.goalPicture.altText} className={style.miniImage} /></div>
          <button onClick={() => setIsOpen(false)} className={style.modalBtn}>Close</button>
        </Modal>

        <Modal
          isOpen={modalIsOpen2}
          style={customStyles}

        >
          <div ><img src={personeel2} alt={data?.goalPicture.altText} className={style.minisrc} /></div>
          <button onClick={() => setIsOpen2(false)} className={style.modalBtn}>Close</button>
        </Modal>


        <Modal
          isOpen={modalIsOpen3}
          style={customStyles}

        >
          <div ><img src={personeel3} alt={data?.goalPicture.altText} className={style.miniImage} /></div>
          <button onClick={() => setIsOpen3(false)} className={style.modalBtn}>Close</button>
        </Modal>


        <Modal
          isOpen={modalIsOpen4}
          style={customStyles}

        >
          <div ><img src={personeel4} alt={data?.goalPicture.altText} className={style.miniImage} /></div>
          <button onClick={() => setIsOpen4(false)} className={style.modalBtn}>Close</button>
        </Modal>


        <Modal
          isOpen={modalIsOpen5}
          style={customStyles}
        >
          <div ><img src={sfeer1} alt={data?.goalPicture.altText} className={style.miniImage} /></div>
          <button onClick={() => setIsOpen5(false)} className={style.modalBtn}>Close</button>
        </Modal>


        <Modal
          isOpen={modalIsOpen6}
          style={customStyles}
        >
          <div ><img src={sfeer2} alt={data?.goalPicture.altText} className={style.miniImage} /></div>
          <button onClick={() => setIsOpen6(false)} className={style.modalBtn}>Close</button>
        </Modal>

        <Modal
          isOpen={modalIsOpen7}
          style={customStyles}
        >
          <div ><img src={sfeer3} alt={data?.goalPicture.altText} className={style.miniImage} /></div>
          <button onClick={() => setIsOpen7(false)} className={style.modalBtn}>Close</button>
        </Modal>

        <Modal
          isOpen={modalIsOpen8}
          style={customStyles}
        >
          <div ><img src={sfeer4} alt={data?.goalPicture.altText} className={style.miniImage} /></div>
          <button onClick={() => setIsOpen8(false)} className={style.modalBtn}>Close</button>
        </Modal>

      </div>
    </Layout>
  );
}
